ocused', 'https://www.aperam.com/aperam-ventures/', 'Worldwide', '["Pre-seed","Seed"]'::jsonb, '["AI/ML","Cleantech","Energy","Materials"]'::jsonb, '2025-12-25T17:37:09.853Z', 'com_1dd91485-c0ed-4946-8fb7-70d8ee866c1e', 'grp_aa703c07-2212-4f88-b0ac-19d79adbbf2b', '["grp_aa703c07-2212-4f88-b0ac-19d79adbbf2b","grp_62ed75ea-252a-4b2f-8f23-a9da0c20983d"]'::jsonb, '2025-12-25T19:20:32.825Z', 'folk', '2025-12-25T19:20:32.826Z', 'Luxembourg', 'AI/ML, Cleantech, Energy, Materials', '[]'::jsonb, '[]'::jsonb, '[]'::jsonb, '["https://www.aperam.com/aperam-ventures/"]'::jsonb, '{"dtOnly":"Yes","dtDeals":null,"fundSize":null,"maxTicket":"â‚¬2M","minTicket":"â‚¬200K","rawStages":["Pre-seed","Seed"],"rawLocations":["Worldwide"],"parentCompany":"Aperam","rawIndustries":["AI/ML","Cleantech","Energy","Materials"]}'::jsonb, 'Corporate VC', 'not_enriched') ON CONFLICT (id) DO UPDATE SET name = EXCLUDED.name, description = EXCLUDED.description, website = EXCLUDED.website, location = EXCLUDED.location, stages = EXCLUDED.stages, sectors = EXCLUDED.sectors, created_at = EXCLUDED.created_at, folk_id = EXCLUDED.folk_id, folk_workspace_id = EXCLUDED.folk_workspace_id, folk_list_ids = EXCLUDED.folk_list_ids, folk_updated_at = EXCLUDED.folk_updated_at, source = EXCLUDED.source, updated_at = EXCLUDED.updated_at, hq_location = EXCLUDED.hq_location, industry = EXCLUDED.industry, emails = EXCLUDED.emails, phones = EXCLUDED.phones, addresses = EXCLUDED.addresses, urls = EXCLUDED.urls, folk_custom_fields = EXCLUDED.folk_custom_fields, firm_classification = EXCLUDED.firm_classification, enrichment_status = EXCLUDED.enrichment_status;
