od & Ag"]'::jsonb, '2025-12-25T18:32:20.267Z', 'com_c4a62ac5-3401-46c3-92b2-9c9b27dbab1f', 'grp_aa703c07-2212-4f88-b0ac-19d79adbbf2b', '["grp_ae60bf78-cc82-478f-8b81-ecef406dc0dd","grp_aa703c07-2212-4f88-b0ac-19d79adbbf2b","grp_b6775cbc-29e9-4e71-b742-e0498331dee8","grp_62ed75ea-252a-4b2f-8f23-a9da0c20983d"]'::jsonb, '2025-12-25T19:20:34.552Z', 'folk', '2025-12-25T19:20:34.554Z', 'Italy', 'Food & Ag', '[]'::jsonb, '[]'::jsonb, '[]'::jsonb, '["https://www.greysiloventures.com"]'::jsonb, '{"dtOnly":"No","dtDeals":null,"fundSize":"--","maxTicket":null,"minTicket":null,"rawStages":["Seed","Series A"],"rawLocations":["Europe"],"parentCompany":"Cereal Docks","rawIndustries":["Food & Ag"]}'::jsonb, 'Corporate VC', 'not_enriched') ON CONFLICT (id) DO UPDATE SET name = EXCLUDED.name, description = EXCLUDED.description, website = EXCLUDED.website, location = EXCLUDED.location, stages = EXCLUDED.stages, sectors = EXCLUDED.sectors, created_at = EXCLUDED.created_at, folk_id = EXCLUDED.folk_id, folk_workspace_id = EXCLUDED.folk_workspace_id, folk_list_ids = EXCLUDED.folk_list_ids, folk_updated_at = EXCLUDED.folk_updated_at, source = EXCLUDED.source, updated_at = EXCLUDED.updated_at, hq_location = EXCLUDED.hq_location, industry = EXCLUDED.industry, emails = EXCLUDED.emails, phones = EXCLUDED.phones, addresses = EXCLUDED.addresses, urls = EXCLUDED.urls, folk_custom_fields = EXCLUDED.folk_custom_fields, firm_classification = EXCLUDED.firm_classification, enrichment_status = EXCLUDED.enrichment_status;
