INSERT INTO investment_firms (id, name, description, website, stages, sectors, linkedin_url, created_at, folk_id, folk_workspace_id, folk_list_ids, folk_updated_at, source, updated_at, hq_location, industry, emails, phones, addresses, urls, folk_custom_fields, enrichment_status) VALUES ('50aa244c-88fb-4bce-8dab-70d6d46fba9d', 'Navus Ventures', 'Navus Ventures B.V. (“Navus”) is a venture capital firm aimed at high-tech and sustainable start-ups and scale-ups, part of a family office and linked to Lely (farming innovators). Founded in 2013, Navus has invested across a range of sectors and is now primarily focused on the ongoing transitions in food and energy. As a family funded VC, Navus is not subject to entry or exit pressure, invests both in typical VC setup as well as for longer holding periods and has simple governance allowing for quick decisions. Navus draws from the knowledge and network of the Lely family business, with a longstanding history in high-tech product development and successful market entry of innovations (but is not linked to the Lely business strategy). Our approach is hands-on and pragmatic, as an independent fund we can respond well to changing situations and act quickly and we support our portfolio as active shareholders with short communication lines to support management on strategic and operational matters. We value transparency and honesty in our relationship with start-ups and fellow investors.', 'https://navusventures.com/', '[]'::jsonb, '[]'::jsonb, 'https://www.linkedin.com/company/navus-ventures/', '2025-12-25T17:36:54.051Z', 'com_540ec74a-67a3-4774-8a3f-d73b40e0c751', 'grp_62ed75ea-252a-4b2f-8f23-a9da0c20983d', '["grp_62ed75ea-252a-4b2f-8f23-a9da0c20983d"]'::jsonb, '2025-12-25T17:36:54.051Z', 'folk', '2025-12-25T17:36:54.051Z', 'Netherlands', 'Food, Energy', '[]'::jsonb, '[]'::jsonb, '[]'::jsonb, '[]'::jsonb, '{"Title":null,"Status":null,"Fund HQ":null,"Website":"https://navusventures.com/","Linkedin":"https://www.linkedin.com/compa